// === Configurações ===
const LINK_AGENDAMENTO = 'https://calendar.app.google/akYrztVhFG9THzyd7';
const POSICAO = 'home_hero';

// === Envia evento e só depois abre o link ===
function abrirAgenda() { window.open(LINK_AGENDAMENTO, '_blank', 'noopener'); }

try {
    if (window.gtag) {
        gtag('event', 'agendar_click', {
            cta_position: POSICAO,
            debug_mode: true,             // facilita aparecer no DebugView
            event_callback: abrirAgenda    // abre somente após o envio
        });
        // fallback se o callback não disparar (ex.: bloqueador) — 800ms é seguro
        setTimeout(abrirAgenda, 800);
    } else {
        abrirAgenda(); // se GA4 não carregou, ao menos abre o link
    }
} catch (e) {
    abrirAgenda();
}
